mapper.insert:

insert into branch_area (id, tenant_id, branch_id,
  code, name, memo, create_by, 
  create_at, last_update_by, last_update_at, 
  is_deleted, version, local_id
  )
values (#{id,jdbcType=BIGINT}, #{tenantId,jdbcType=BIGINT}, #{branchId,jdbcType=BIGINT}, 
  #{code,jdbcType=CHAR}, #{name,jdbcType=VARCHAR}, #{memo,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, 
  #{createAt,jdbcType=TIMESTAMP}, #{lastUpdateBy,jdbcType=VARCHAR}, #{lastUpdateAt,jdbcType=TIMESTAMP}, 
  #{isDeleted,jdbcType=BIT}, #{version,jdbcType=BIGINT}, #{localId,jdbcType=BIGINT}
  )

mapper.select:
SELECT count(1) FROM branch_area b WHERE b.tenant_id=${tenantId} AND b.branch_id=${branchId} AND is_deleted=0
AND name=#{name}

  AND id!=#{id}
