mapper.select:
SELECT bt.*,ba.name area_name
FROM
branch_table bt
INNER JOIN branch_area ba ON ba.id=bt.branch_area_id
AND ba.tenant_id=bt.tenant_id
AND ba.branch_id=bt.branch_id
AND bt.is_deleted=0
AND bt.tenant_id=${tenantId}
AND bt.branch_id=${branchId}

  AND bt.branch_area_id=#{areaId}


  AND (bt.code LIKE '%${codeOrName}%' OR bt.name LIKE '%${codeOrName}%')


  LIMIT ${offset},${rows}

SELECT count(1)
FROM
branch_table bt
WHERE bt.is_deleted=0
AND bt.tenant_id=${tenantId}
AND bt.branch_id=${branchId}

  AND bt.branch_area_id=#{areaId}


  AND (bt.code LIKE '%${codeOrName}%' OR bt.name LIKE '%${codeOrName}%')

SELECT count(1) FROM branch_table b WHERE b.tenant_id=${tenantId} AND b.branch_id=${branchId} AND is_deleted=0
AND name=#{name}

  AND id!=#{id}

SELECT count(1) FROM branch_table b WHERE b.tenant_id=${tenantId} AND b.branch_id=${branchId} AND is_deleted=0
AND code=#{code}

  AND id!=#{id}
