mapper.select:
SELECT * FROM diet_promotion_amount dpsr WHERE is_deleted=0

  AND dpsr.id =#{id}


  AND dpsr.diet_promotion_id =#{dietPromotionId}

SELECT * FROM diet_promotion_amount dpa WHERE is_deleted=0

  AND dpa.id =#{id}


  AND dpa.diet_promotion_id =#{dietPromotionId}


  AND dpa.tenant_id =${tenantId}


  LIMIT ${offset},${rows}
