mapper.select:
SELECT * FROM diet_promotion_festival dpf WHERE is_deleted=0

  AND dpf.id =#{id}


  AND dpf.diet_promotion_id =#{dietPromotionId}


  AND dpf.tenant_id =${tenantId}


  AND dpf.activity_name=#{activityName}


  AND dpf.card_id =#{cardId}


  AND dpf.precision_use =#{precisionUse}


  LIMIT ${offset},${rows}

