mapper.select:
SELECT * FROM diet_promotion dp WHERE is_deleted=0

    AND dp.id =#{id}


    AND dp.id!=#{promotionId}


    AND dp.tenant_id=${tenantId}


    AND dp.promotion_type=#{promotionType}

SELECT * FROM diet_promotion dp WHERE is_deleted=0

  AND dp.id =#{id}


  AND dp.id!=#{promotionId}


  AND dp.tenant_id=${tenantId}


  AND dp.promotion_type=#{promotionType}


  AND (dp.promotion_name LIKE '%${promotionName}%')


  AND (dp.promotion_name LIKE '%${activityName}%')


  AND dp.promotion_type=12


  AND (dp.promotion_type!=8 AND dp.promotion_type!=12)



  AND (dp.is_use=1 AND date_format(concat(dp.start_date,' ',dp.start_time),'%Y-%c-%d %h:%i:%s')<=now() AND date_format(concat(dp.end_date,' ',dp.end_time),'%Y-%c-%d %h:%i:%s')>=now())



  AND (dp.is_use=1 AND data_format(concat(dp.stat_date,' ',dp.start_time),'%Y-%c-%d %h:%i:%s')>=now())



  AND (dp.is_use=0 or data_format(concat(dp.end_date,' ',dp.end_time),'%Y-%c-%d %h:%i:%s')<=now())











  LIMIT ${offset},${rows}

SELECT * FROM diet_promotion dp WHERE is_deleted=0

  AND dp.id =#{id}


  LIMIT ${offset},${rows}

SELECT * FROM diet_promotion dp WHERE is_deleted=0 AND dp.promotion_type = 8

  AND dp.id =#{id}


  AND dp.id!=#{promotionId}


  AND dp.tenant_id=${tenantId}


  AND (dp.promotion_name LIKE '%${promotionName}%')



  AND (dp.is_use=1 AND date_format(concat(dp.start_date,' ',dp.start_time),'%Y-%c-%d %h:%i:%s')<=now() AND date_format(concat(dp.end_date,' ',dp.end_time),'%Y-%c-%d %h:%i:%s')>=now())



  AND (dp.is_use=1 AND data_format(concat(dp.stat_date,' ',dp.start_time),'%Y-%c-%d %h:%i:%s')>=now())



  AND (dp.is_use=0 or data_format(concat(dp.end_date,' ',dp.end_time),'%Y-%c-%d %h:%i:%s')<=now())



  AND dp.create_at >= '${beginTime}'




AND dp.create_at <= '${endTime}'



  LIMIT ${offset},${rows}
