mapper.select:
SELECT * FROM diet_promotion_total_reduce dptr WHERE is_deleted=0

  AND dptr.id =#{id}


  AND dptr.diet_promotion_id =#{dietPromotionId}


  AND dptr.tenant_id =${tenantId}


  LIMIT ${offset},${rows}

SELECT dptr.*,ct.cat_name catName,ct.cat_code catCode FROM diet_promotion_total_reduce dptr left JOIN category ct on dptr.category_id = ct.id WHERE dptr.is_deleted=0

  AND dptr.id =#{id}


  AND dptr.diet_promotion_id =#{dietPromotionId}


  AND dptr.tenant_id =${tenantId}


  LIMIT ${offset},${rows}

SELECT * FROM diet_promotion_total_reduce dptr WHERE is_deleted=0

  AND dptr.id =#{id}


  AND dptr.tenant_id =${tenantId}


  AND dptr.diet_promotion_id =#{dietPromotionId}


  LIMIT ${offset},${rows}
