mapper.select:
SELECT gs.*,sg.`name` group_name FROM goods_spec gs
INNER JOIN spec_group sg
ON gs.group_id=sg.id
AND sg.tenant_id=${tenantId} AND sg.is_deleted=0
AND gs.tenant_id=${tenantId} AND gs.is_deleted=0



      AND gs.branch_id = ${branchId}


      AND (gs.branch_id = ${branchId} OR gs.branch_id = (SELECT id FROM branch b WHERE b.tenant_id = ${tenantId} AND b.branch_type = 0))




  AND gs.branch_id = ${branchId}


   AND gs.group_id=#{groupId}


  AND gs.property LIKE '%${goodsSpecName}%'

LIMIT ${offset},${rows}

SELECT count(1) FROM goods_spec WHERE tenant_id=${tenantId} AND is_deleted=0



      AND branch_id = ${branchId}


      AND (branch_id = ${branchId} OR branch_id = (SELECT id FROM branch b WHERE b.tenant_id = ${tenantId} AND b.branch_type = 0))




  AND branch_id = ${branchId}


  AND group_id=#{groupId}


  AND property LIKE '%${goodsSpecName}%'

SELECT COUNT(1) FROM goods_spec WHERE tenant_id=${tenantId} AND group_id=#{groupId} AND is_deleted=0 AND property=#{property}

  AND id!=#{id}


  AND branch_id = ${branchId}


  AND (branch_id = ${branchId} OR branch_id=(SELECT id FROM branch WHERE tenant_id=#{tenantId} AND is_deleted=0 AND branch_type=0))
