mapper.insert:

insert into goods_unit (id, unit_code, unit_name,
  create_by, create_at, last_update_by,
  last_update_at, is_deleted, tenant_id,
  branch_id, version, local_id
  )
values (#{id,jdbcType=BIGINT}, #{unitCode,jdbcType=CHAR}, #{unitName,jdbcType=VARCHAR},
  #{createBy,jdbcType=VARCHAR}, #{createAt,jdbcType=TIMESTAMP}, #{lastUpdateBy,jdbcType=VARCHAR},
  #{lastUpdateAt,jdbcType=TIMESTAMP}, #{isDeleted,jdbcType=BIT}, #{tenantId,jdbcType=BIGINT},
  #{branchId,jdbcType=BIGINT}, #{version,jdbcType=BIGINT}, #{localId,jdbcType=BIGINT}
  )

mapper.select:
SELECT * from goods_unit t where t.is_deleted = 0
AND t.tenant_id=#{tenantId}
AND t.branch_id = #{branchId}

  AND (t.unit_code like '%${codeName}%' or t.unit_name like '%${codeName}%')


  AND t.unit_name=#{name}


  ORDER BY t.last_update_at DESC


  ORDER BY t.${field} ${order}


  LIMIT ${offset},${rows}

SELECT count(1) from goods_unit t where t.is_deleted = 0
AND t.tenant_id=#{tenantId}
AND t.branch_id = #{branchId}

  AND (t.unit_code like '%${codeName}%' or t.unit_name like '%${codeName}%')


  AND t.unit_name=#{name}


      SELECT * from goods_unit t where t.is_deleted = 0
      AND t.tenant_id=#{tenantId,jdbcType=BIGINT}


AND (t.unit_code like '%${codeName}%' or t.unit_name like '%${codeName}%')


AND t.id <> ${unitId}


AND t.unit_name=#{name}


AND t.branch_id = #{branchId}


ORDER BY t.last_update_at DESC


       LIMIT #{offset,jdbcType=INTEGER},#{rows,jdbcType=INTEGER}


      SELECT count(1) FROM (
      SELECT * from goods_unit t where t.is_deleted = 0
      AND t.tenant_id=#{tenantId,jdbcType=BIGINT}


AND (t.unit_code like '%${codeName}%' or t.unit_name like '%${codeName}%')


AND t.unit_name=#{name}


AND t.id <> ${unitId}


AND t.branch_id = #{branchId}


   ) rows_

SELECT * FROM goods_unit WHERE is_deleted=0

  AND tenant_id = ${tenantId}


  AND branch_id = #{branchId}

SELECT count(1) FROM goods_unit g WHERE g.is_deleted=0 AND g.tenant_id=${tenantId} AND g.unit_name=#{unitName}

  AND g.id!=#{id}


  AND g.branch_id = ${branchId}

SELECT MAX(b.unit_code) FROM goods_unit b WHERE b.tenant_id=${tenantId}

    AND b.branch_id = #{branchId}

AND b.is_deleted=0
